#!/bin/sh
# Ruby Pre-Commit Hook
# Generated by @hivellm/rulebook

echo "ğŸ” Running Ruby pre-commit checks..."

# Lint
if command -v rubocop &> /dev/null; then
  echo "  â†’ Linting..."
  rubocop || {
    echo "âŒ RuboCop failed"
