  }
elif command -v rake &> /dev/null; then
  echo "  â†’ Running tests..."
  bundle exec rake test || {
    echo "âŒ Tests failed"
    exit 1
  }
fi

echo "âœ… All Ruby pre-commit checks passed!"
exit 0

